import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';

const FoodItems = () => {
  const [foodItems, setFoodItems] = useState([]);
  const [cart, setCart] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchFoodItems = async () => {
      try {
        const response = await axios.get('/api/food');
        const items = response.data.map(item => ({
          ...item,
          images: item.images || [] // Ensure images is an array
        }));
        setFoodItems(items);
      } catch (error) {
        console.error(error);
      }
    };

    fetchFoodItems();
  }, []);

  const addToCart = (item) => {
    setCart([...cart, item]);
  };

  const goToCart = () => {
    navigate('/cart', { state: { cart } });
  };

  return (
    <div>
      <h2 className="text-2xl font-bold mb-4">Food Items</h2>
      <ul className="space-y-4">
        {foodItems.length > 0 ? (
          foodItems.map((item) => (
            <li key={item.id} className="bg-white p-4 rounded-lg shadow-md flex flex-col items-center">
              <Carousel showThumbs={false} className="w-full mb-4">
                {item.images.map((image, index) => (
                  <div key={index}>
                    <img src={image} alt={item.name} />
                  </div>
                ))}
              </Carousel>
              <div className="flex-1 text-center">
                <h3 className="text-lg font-bold">{item.name}</h3>
                <p>${item.price}</p>
              </div>
              <button
                onClick={() => addToCart(item)}
                className="bg-blue-500 text-white p-2 rounded mt-4"
              >
                Add to Cart
              </button>
            </li>
          ))
        ) : (
          <li className="bg-white p-4 rounded-lg shadow-md">No food items available</li>
        )}
      </ul>
      <button
        onClick={goToCart}
        className="mt-4 bg-green-500 text-white p-2 rounded"
      >
        Go to Cart
      </button>
    </div>
  );
};

export default FoodItems;
